import { useState, useEffect } from 'react';
import './App.css';
import axios from 'axios'
import Header from './components/header'

type SearchUserType = {
  login: string
}
type SearchResult = {
  items: SearchUserType[]
}

type UserType = {
  login: string
  id: number
  avatar_url: string
  followers: number
}

function App() {
  let [selectedUser, setSelectedUser] = useState<SearchUserType | null>(null)
  let [userDetails, setUserDetails] = useState<UserType | null>(null)
  let [users, setUsers] = useState<SearchUserType[]>([])
  let [tempSearch, setTempSearch] = useState<string>('it')
  let [searchTerm, setSearchTerm] = useState<string>('it')

  useEffect(() => {
    if(selectedUser)
      document.title = selectedUser.login
  }, [selectedUser])

  const fetch_data = (term: string) => {
    axios.get<SearchResult>(`https://api.github.com/search/users?q=${term}`)
    .then(res => {
      setUsers(res.data.items)
    })
  }

  useEffect(() => {
    console.log('sync_users')
    fetch_data(searchTerm)
  }, [searchTerm])

  useEffect(() => {
    console.log('sync_users_details')
    if(!!selectedUser)
      axios.get<UserType>(`https://api.github.com/users/${selectedUser.login}`)
      .then(res => {
        setUserDetails(res.data)
      })
  }, [selectedUser])

  return (
    <div style={{display:'flex'}}>
      <div>
        <Header tempSearch={tempSearch} setTempSearch={setTempSearch} setSearchTerm={setSearchTerm} />
        <div>
          <input placeholder="search" value={tempSearch} onChange={ev => setTempSearch(ev.currentTarget.value)} />
          <button onClick={() => {
            setSearchTerm(tempSearch)
          }}>find</button>
        </div>
        <ul>
          {users.map(u => <li
            key={u.login}
            style={{backgroundColor:(selectedUser?.login === u.login) ? 'gray' : ''}}
            onClick={() => {
            setSelectedUser(u)
          }}>{u.login}</li>)}
        </ul>
      </div>
      <div>
        <h2>username</h2>
        {userDetails && <div>
          <img src={userDetails.avatar_url} alt="" />
          <br />
          {userDetails.login}, followers: {userDetails.followers}
        </div>}
      </div>
    </div>
  );
}

export default App;
