import { useState, useEffect } from 'react';
import './App.css';
import axios from 'axios'

type SearchUserType = {
  login: string
}
type SearchResult = {
  items: SearchUserType[]
}

function App() {
  let [selectedUser, setSelectedUser] = useState<string | null>(null)
  let [users, setUsers] = useState<SearchUserType[]>([])

  useEffect(() => {
    if(selectedUser)
      document.title = selectedUser
  }, [selectedUser])

  useEffect(() => {
    axios.get<SearchResult>('https://api.github.com/search/users?q=it-kamasutra')
    .then(res => {
      setUsers(res.data.items)
    })
  }, [])

  return (
    <div style={{display:'flex'}}>
      <div>
        <div>
          <input placeholder="search" />
          <button>find</button>
        </div>
        <ul>
          {users.map(u => <li
            key={u.login}
            style={{backgroundColor:(selectedUser === u.login) ? 'gray' : ''}}
            onClick={() => {
            setSelectedUser(u.login)
          }}>{u.login}</li>)}
        </ul>
      </div>
      <div>
        <h2>username</h2>
        <div>details</div>
      </div>
    </div>
  );
}

export default App;
